import React, { useState } from 'react';
import '../css/header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faUser, faHeart, faPlus } from '@fortawesome/free-solid-svg-icons';
import AddCardModal from './AddModelCard';

const Header = ({ onSearch }) => {
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <>
      <header className="header">
        <div className="header-container">
            <form className="search-bar" onSubmit={handleSearch}>
              <input 
                type="text" 
                placeholder="Search tasks..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </form>
          </div>

          <div className="icons">
            <a href="#" className="icon-link" aria-label="User account">
              <FontAwesomeIcon icon={faUser} />
            </a>
            <a href="#" className="icon-link" aria-label="Wishlist">
              <FontAwesomeIcon icon={faHeart} />
            </a>
            <button 
              className="icon-link plus-icon" 
              aria-label="Add new card" 
              onClick={() => setShowModal(true)}
            >
              <FontAwesomeIcon icon={faPlus} />
            </button>
          </div>
      </header>

      {showModal && <AddCardModal setShowModal={setShowModal} onClose={handleModalClose} />}
    </>
  );
};

export default Header;
