import React from 'react';
import '../css/card.css'; // Import the CSS file

const cardData = [
    {
      img: "https://images.pexels.com/photos/1220757/pexels-photo-1220757.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      name: "David James",
      github: "#",
      instagram: "#",
      twitter: "#",
    },
    {
      img: "https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      name: "Sophia Brown",
      github: "#",
      instagram: "#",
      twitter: "#",
    },
    {
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      name: "Michael Smith",
      github: "#",
      instagram: "#",
      twitter: "#",
    },
    {
      img: "https://images.pexels.com/photos/2379005/pexels-photo-2379005.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      name: "Emily Johnson",
      github: "#",
      instagram: "#",
      twitter: "#",
    },
    {
      img: "https://images.pexels.com/photos/614810/pexels-photo-614810.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      name: "William Taylor",
      github: "#",
      instagram: "#",
      twitter: "#",
    },
    {
      img: "https://images.pexels.com/photos/91227/pexels-photo-91227.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      name: "Olivia Martinez",
      github: "#",
      instagram: "#",
      twitter: "#",
    },
  ];
  
  const ProfileHover = () => {
    return (
      <div className="profile-container">
        {cardData.map((card, index) => (
          <div className="img-wrapper" key={index}>
            <h2>{card.name}</h2>
            <ul className="side-icons">
              <li><a href={card.github}><i className="fab fa-github"></i></a></li>
              <li><a href={card.instagram}><i className="fab fa-instagram"></i></a></li>
              <li><a href={card.twitter}><i className="fab fa-twitter"></i></a></li>
            </ul>
            <img src={card.img} alt={card.name} />
            <div className="bottom-circle">
              <i className="fas fa-plus"></i>
            </div>
          </div>
        ))}
      </div>
    );
  };
  
  export default ProfileHover;
