import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/card.css';
import '../css/cardModal.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faCheckCircle, faTrash } from '@fortawesome/free-solid-svg-icons';
import Header from './Header';
import Sidebar from './NavBar';

const ProfileHover = () => {
  const [todos, setTodos] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [currentTodo, setCurrentTodo] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activePriorityFilter, setActivePriorityFilter] = useState(null);
  const [activeTagFilter, setActiveTagFilter] = useState(null);

  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchData = async () => {
      try {
        const usersResponse = await axios.get('http://localhost:5000/api/v1/users/');
        const todoResponse = await axios.get('http://localhost:5000/api/v1/todos/todo');

        if (!usersResponse.data.success || !todoResponse.data.success) {
          throw new Error('Failed to fetch data');
        }

        const userData = {};
        usersResponse.data.data.forEach(user => {
          userData[user._id] = user.username;
        });

        setUserMap(userData);
        setAllUsers(usersResponse.data.data);
        setTodos(todoResponse.data.data);
      } catch (err) {
        setError(err.message || 'Error fetching data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const filteredTodos = todos
    .filter(todo => {
      const searchLower = searchTerm.toLowerCase();
      return (
        todo.title.toLowerCase().includes(searchLower) ||
        (todo.description && todo.description.toLowerCase().includes(searchLower)) ||
        (todo.tags && todo.tags.some(tag => tag.toLowerCase().includes(searchLower))) ||
        (todo.mentionedUsers &&
          todo.mentionedUsers.some(userId =>
            userMap[userId]?.toLowerCase().includes(searchLower)
          ))
      );
    })
    .filter(todo => !activePriorityFilter || todo.priority?.toLowerCase() === activePriorityFilter)
    .filter(todo => !activeTagFilter || todo.tags?.includes(activeTagFilter));

  const handlePriorityFilter = (priority) => {
    setActivePriorityFilter(priority);
    setActiveTagFilter(null);
  };

  const handleTagFilter = (tag) => {
    setActiveTagFilter(tag);
    setActivePriorityFilter(null);
  };

  const handleUpdate = (todo) => {
    setCurrentTodo(todo);
    setShowUpdateModal(true);
  };

  const handleUpdateSubmit = async (updatedData) => {
    try {
      const response = await axios.patch(
        `http://localhost:5000/api/v1/todos/${currentTodo._id}`,
        updatedData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      if (response.data.success) {
        setTodos(todos.map(todo =>
          todo._id === currentTodo._id ? response.data.data : todo
        ));
        setShowUpdateModal(false);
        alert('Task updated successfully!');
      }
    } catch (err) {
      alert(`Update failed: ${err.response?.data?.message || err.message}`);
    }
  };

  const handleComplete = async (todoId) => {
    try {
      const response = await axios.patch(
        `http://localhost:5000/api/v1/todos/${todoId}`,
        { status: 'completed' },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      if (response.data.success) {
        setTodos(todos.map(todo =>
          todo._id === todoId ? response.data.data : todo
        ));
        alert('Task marked as completed!');
      }
    } catch (err) {
      alert(`Failed to complete task: ${err.response?.data?.message || err.message}`);
    }
  };

  const handleDelete = async (todoId) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/v1/todos/${todoId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      setTodos(todos.filter(todo => todo._id !== todoId));
      alert('Task deleted successfully!');
    } catch (err) {
      alert(`Delete failed: ${err.response?.data?.message || err.message}`);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <>
      <Sidebar onPriorityFilter={handlePriorityFilter} onTagFilter={handleTagFilter} />
      <Header onSearch={handleSearch} />

      <div className="card-container">
        {filteredTodos.map(todo => (
          <div key={todo._id} className="img-wrapper">
            <h2>{todo.title}</h2>

            <ul className="side-icons">
              <li title="Update" onClick={() => handleUpdate(todo)}>
                <FontAwesomeIcon icon={faPenToSquare} />
              </li>
              <li title="Complete" onClick={() => handleComplete(todo._id)}>
                <FontAwesomeIcon icon={faCheckCircle} />
              </li>
            </ul>

            <div className="card-content">
              <p className="description">{todo.description || 'N/A'}</p>

              <div className={`priority-box ${todo.priority?.toLowerCase()}`}>
                {todo.priority}
              </div>

              {todo.tags?.length > 0 && (
                <div className="tags">
                  {todo.tags.map((tag, index) => (
                    <span key={index} className="tag-item">{tag}</span>
                  ))}
                </div>
              )}

              {todo.mentionedUsers?.length > 0 && (
                <div className="mentions-container">
                  <span className="mention-label">Mentions:</span>
                  {todo.mentionedUsers
                    .filter(userId => userMap[userId])
                    .map(userId => (
                      <span key={userId} className="user-mention">
                        @{userMap[userId]}
                      </span>
                    ))}
                </div>
              )}

              {todo.notes?.length > 0 && (
                <div className="notes-section">
                  <strong>Notes:</strong>
                  <ul>
                    {todo.notes.map((note, index) => (
                      <li key={index}>{note.content || 'No content'}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>

            <div className="bottom-circle" title="Delete" onClick={() => handleDelete(todo._id)}>
              <FontAwesomeIcon icon={faTrash} />
            </div>
          </div>
        ))}
      </div>

      {showUpdateModal && (
        <UpdateCardPopup
          todo={currentTodo}
          allUsers={allUsers}
          onClose={() => setShowUpdateModal(false)}
          onSubmit={handleUpdateSubmit}
        />
      )}
    </>
  );
};

const UpdateCardPopup = ({ todo, allUsers, onClose, onSubmit }) => {
  const [title, setTitle] = useState(todo?.title || '');
  const [description, setDescription] = useState(todo?.description || '');
  const [priority, setPriority] = useState(todo?.priority || 'Medium');
  const [tags, setTags] = useState(todo?.tags || []);
  const [tagInput, setTagInput] = useState('');
  const [mentionedUsers, setMentionedUsers] = useState(todo?.mentionedUsers || []);
  const [note, setNote] = useState(todo?.notes?.[0]?.content || '');
  const [selectedUser, setSelectedUser] = useState('');

  const handleAddTag = () => {
    if (tagInput.trim()) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
    }
  };

  const handleAddUser = () => {
    if (selectedUser && !mentionedUsers.includes(selectedUser)) {
      setMentionedUsers([...mentionedUsers, selectedUser]);
      setSelectedUser('');
    }
  };

  const handleRemoveUser = (id) => {
    setMentionedUsers(mentionedUsers.filter(userId => userId !== id));
  };

  const handleSubmit = () => {
    const updatedData = {
      title,
      description,
      priority,
      tags,
      mentionedUsers,
      notes: note ? [{ content: note }] : [],
    };
    onSubmit(updatedData);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Update Task</h2>
        <input value={title} onChange={e => setTitle(e.target.value)} placeholder="Title" />
        <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Description" />
        <select value={priority} onChange={e => setPriority(e.target.value)}>
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>

        <div className="tag-section">
          <input
            value={tagInput}
            onChange={e => setTagInput(e.target.value)}
            placeholder="Add tag"
          />
          <button onClick={handleAddTag}>Add Tag</button>
          <div className="tag-list">
            {tags.map((tag, idx) => <span key={idx}>{tag}</span>)}
          </div>
        </div>

        <div className="mention-section">
          <select value={selectedUser} onChange={e => setSelectedUser(e.target.value)}>
            <option value="">Select user to mention</option>
            {allUsers.map(user => (
              <option key={user._id} value={user._id}>{user.username}</option>
            ))}
          </select>
          <button onClick={handleAddUser}>Mention</button>
          <div className="mention-list">
            {mentionedUsers.map(id => (
              <span key={id}>
                @{allUsers.find(u => u._id === id)?.username || id}
                <button onClick={() => handleRemoveUser(id)}>x</button>
              </span>
            ))}
          </div>
        </div>

        <textarea
          value={note}
          onChange={e => setNote(e.target.value)}
          placeholder="Add a note (optional)"
        />

        <div className="modal-actions">
          <button onClick={handleSubmit}>Update</button>
          <button onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default ProfileHover;
