import React, { useState, useEffect } from 'react';
import '../css/card.css';

const Card = ({ title, description, priority, tags, mentionedUsers }) => {
  return (
    <div className="img-wrapper">
      <h2>{title}</h2>
      <p><strong>Description:</strong> {description}</p>
      <p><strong>Priority:</strong> {priority}</p>
      <p><strong>Tags:</strong> {tags.join(', ')}</p>
      <p><strong>Mentioned Users:</strong> 
        {mentionedUsers.length > 0 ? mentionedUsers.map(user => user.username).join(', ') : 'No users mentioned'}
      </p>
    </div>
  );
};

const ProfileHover = () => {
  const [cards, setCards] = useState([]); // Default to an empty array

  // Fetch todos from the database
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/v1/todos');
        const data = await response.json();

        // Check if the response is an array before updating the state
        if (Array.isArray(data)) {
          setCards(data);
        } else {
          console.error('Expected an array but got:', data);
        }
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };
    fetchTodos();
  }, []); // Empty dependency array to fetch data once on mount

  return (
    <div className="card-container">
      {Array.isArray(cards) && cards.length > 0 ? (
        cards.map((card) => (
          <Card 
            key={card._id}  // Assuming the API returns an _id for each todo item
            title={card.title}
            description={card.description}
            priority={card.priority}
            tags={card.tags}
            mentionedUsers={card.mentionedUsers}
          />
        ))
      ) : (
        <p>No cards available</p> // If no cards, show a message
      )}
    </div>
  );
};

export default ProfileHover;
