import React, { useState, useEffect, useRef } from 'react'; // Import useEffect and useRef
import '../css/header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faUser, faHeart, faPlus } from '@fortawesome/free-solid-svg-icons';
import AddCardModal from './AddModelCard';

// Receive allUsers and onUserSelect as props
const Header = ({ onSearch, allUsers, onUserSelect }) => {
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [showUserDropdown, setShowUserDropdown] = useState(false); // State for user dropdown visibility

  const dropdownRef = useRef(null); // Ref for the dropdown element

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  // Toggle user dropdown visibility
  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  // Handle user selection from the dropdown
  const handleUserClick = (userId) => {
    onUserSelect(userId); // Call the parent's filter function
    setShowUserDropdown(false); // Close the dropdown
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowUserDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);


  return (
    <>
      <header className="header">
        <div className="header-container">
          <div className="icons">
            {/* Add Profile Icon */}
            <div className="icon-link profile-icon-container" ref={dropdownRef}> {/* Add ref here */}
              <button
                className="icon-button" // Use a button for accessibility
                aria-label="View users"
                onClick={toggleUserDropdown}
              >
                <FontAwesomeIcon icon={faUser} />
              </button>
              {/* User Dropdown */}
              {showUserDropdown && (
                <div className="user-dropdown">
                  <div className="dropdown-item" onClick={() => handleUserClick(null)}>
                    All Users
                  </div>
                  {allUsers.map(user => (
                    <div
                      key={user._id}
                      className="dropdown-item"
                      onClick={() => handleUserClick(user._id)}
                    >
                      {user.username || user.name} {/* Display username or name */}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Add Card Icon */}
            <button
              className="icon-link plus-icon"
              aria-label="Add new card"
              onClick={() => setShowModal(true)}
            >
              <FontAwesomeIcon icon={faPlus} />
              <span>ADD CARD</span>
            </button>
          </div>

          <form className="search-bar" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </form>
        </div>

        {showModal && <AddCardModal setShowModal={setShowModal} onClose={handleModalClose} />}
      </header>
    </>
  );
};

export default Header;
