import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/card.css';

const ProfileHover = () => {
  const [todos, setTodos] = useState([]);
  const [userMap, setUserMap] = useState({}); // Stores username by user ID
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch todos first
        const todoResponse = await axios.get('http://localhost:5000/api/v1/todos/todo');
        
        if (!todoResponse.data.success) {
          throw new Error('Failed to fetch todos');
        }

        const todosData = todoResponse.data.data;
        setTodos(todosData);

        // Extract all unique user IDs from mentionedUsers across all todos
        const userIds = [
          ...new Set(
            todosData.flatMap(todo => 
              todo.mentionedUsers || []
            )
          )
        ];

        // Fetch usernames for all mentioned users
        if (userIds.length > 0) {
          const usersResponse = await Promise.all(
            userIds.map(id => 
              axios.get(`http://localhost:5000/api/v1/users/${id}`)
                .then(res => res.data)
                .catch(() => null) // Handle case where user might not exist
            )
          );

          // Create a mapping of user IDs to usernames
          const userData = {};
          usersResponse.forEach(user => {
            if (user && user.success && user.data) {
              userData[user.data._id] = user.data.username;
            }
          });
          setUserMap(userData);
        }
      } catch (err) {
        setError(err.message || 'Error fetching data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="card-container">
      {todos.map((todo) => (
        <div key={todo._id} className="img-wrapper">
          <h2>{todo.title}</h2>
          
          <ul className="side-icons">
            <li title="Priority"><i className="fas fa-star" /></li>
            <li title="Tags"><i className="fas fa-tags" /></li>
            <li title="Notes"><i className="fas fa-sticky-note" /></li>
          </ul>

          <div style={{ padding: '10px', textAlign: 'center', width: '100%' }}>
            <p className="description">{todo.description || 'N/A'}</p>
            
            <div className={`priority-box ${todo.priority.toLowerCase()}`}>
              {todo.priority}
            </div>
            
            {todo.tags && todo.tags.length > 0 && (
              <div className="tags">
                {todo.tags.map((tag, index) => (
                  <span key={index} className="tag-item">
                    {tag}
                  </span>
                ))}
              </div>
            )}

            {todo.mentionedUsers && todo.mentionedUsers.length > 0 && (
              <div className="mentions-container" style={{ margin: '5px 0' }}>
                <span style={{ fontSize: '12px', marginRight: '5px' }}>Mentions:</span>
                {todo.mentionedUsers.map((userId) => (
                  <span key={userId} className="user-mention">
                    @{userMap[userId] || 'unknown'}
                  </span>
                ))}
              </div>
            )}
            
            {todo.notes && todo.notes.length > 0 && (
              <div className="notes-section">
                <strong>Notes:</strong>
                <ul>
                  {todo.notes.map((note, index) => (
                    <li key={index}>
                      {note.content || 'No content'}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div className="bottom-circle" title="More">
            <i className="fas fa-arrow-right" />
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProfileHover;
