import React from 'react';
import '../css/header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faUser, faHeart, faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const Header = () => {
  return (
    <header className="header">
      <div className="header-container">
        {/* Left spacer for balance */}
        <div className="header-spacer"></div>

        {/* Centered Search Bar */}
        <div className="search-container">
          <div className="search-bar">
            <input type="text" placeholder="Search products..." />
            <button className="search-button">
              <FontAwesomeIcon icon={faSearch} />
            </button>
          </div>
        </div>

        {/* Right-aligned Icons */}
        <div className="icons">
          <a href="#" className="icon-link" aria-label="User account">
            <FontAwesomeIcon icon={faUser} />
          </a>
          <a href="#" className="icon-link" aria-label="Wishlist">
            <FontAwesomeIcon icon={faHeart} />
          </a>
          <a href="#" className="icon-link cart-icon" aria-label="Shopping cart">
            <FontAwesomeIcon icon={faShoppingCart} />
            <span className="cart-count">0</span>
          </a>
        </div>
      </div>
    </header>
  );
};

export default Header;
