import React from 'react';
import '../css/header.css';

const Header = () => {
  return (
    <>
      {/* Announcement Bar */}
      <div className="announcement-bar">
        <div className="announcement-slider">
          <p>Free shipping on orders over $50! &nbsp;|&nbsp; New arrivals every week!</p>
        </div>
      </div>

      {/* Header */}
      <header className="header">
        <div className="header-container">
          {/* New Search Bar */}
          <div className="searchBox">
            <input className="searchInput" type="text" placeholder="Search products" />
            <button className="searchButton">
              <i className="material-icons">search</i>
            </button>
          </div>

          {/* Logo */}
          <div className="logo">
            <img src="https://via.placeholder.com/150x50?text=Your+Logo" alt="Company Logo" />
          </div>

          {/* Icons */}
          <div className="icons">
            <a href="#" className="icon-link" aria-label="User account">
              <i className="fas fa-user"></i>
            </a>
            <a href="#" className="icon-link" aria-label="Wishlist">
              <i className="fas fa-heart"></i>
            </a>
            <a href="#" className="icon-link cart-icon" aria-label="Shopping cart">
              <i className="fas fa-shopping-cart"></i>
              <span className="cart-count">0</span>
            </a>
          </div>
        </div>
      </header>
    </>
  );
};

export default Header;
