import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/card.css';
import '../css/cardModal.css';

const ProfileHover = () => {
  const [todos, setTodos] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [currentTodo, setCurrentTodo] = useState(null);
  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all users first
        const usersResponse = await axios.get('http://localhost:5000/api/v1/users/');
        
        if (!usersResponse.data.success) {
          throw new Error('Failed to fetch users');
        }

        // Create mapping of user IDs to usernames
        const userData = {};
        usersResponse.data.data.forEach(user => {
          userData[user._id] = user.username;
        });
        setUserMap(userData);
        setAllUsers(usersResponse.data.data);

        // Then fetch todos
        const todoResponse = await axios.get('http://localhost:5000/api/v1/todos/todo');
        
        if (!todoResponse.data.success) {
          throw new Error('Failed to fetch todos');
        }

        setTodos(todoResponse.data.data);
      } catch (err) {
        setError(err.message || 'Error fetching data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleUpdate = (todo) => {
    setCurrentTodo(todo);
    setShowUpdateModal(true);
  };

  const handleComplete = async (todoId) => {
    try {
      const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4MGY5YzBmMmJmODgzYjM2ZTA3YmViYyIsImlhdCI6MTc0NTkxNzI3OSwiZXhwIjoxNzQ4NTA5Mjc5fQ.5MxYozNKwnfQl8IdbJyc3Pl1F3zrc8L7TpDZ1X-kQwg'; // Use the token directly here
      await axios.patch(
        `http://localhost:5000/api/v1/todos/${todoId}`,
        { status: 'completed' },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setTodos(todos.map(todo => 
        todo._id === todoId ? { ...todo, status: 'completed' } : todo
      ));
    } catch (err) {
      console.error('Error completing todo:', err);
      alert('Failed to complete task');
    }
  };

  const handleUpdateSubmit = async (updatedData) => {
    try {
      const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4MGY5YzBmMmJmODgzYjM2ZTA3YmViYyIsImlhdCI6MTc0NTkxNzI3OSwiZXhwIjoxNzQ4NTA5Mjc5fQ.5MxYozNKwnfQl8IdbJyc3Pl1F3zrc8L7TpDZ1X-kQwg'; // Use the token directly here
      console.log('Updating todo with ID:', currentTodo._id); // Debug log
      
      const response = await axios.patch(
        `http://localhost:5000/api/v1/todos/${currentTodo._id}`, // No extra slashes
        updatedData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        }
      );
      
      console.log('Update response:', response.data); // Debug log
      
      if (response.data.success) {
        setTodos(todos.map(todo => 
          todo._id === currentTodo._id ? response.data.data : todo
        ));
        setShowUpdateModal(false);
      }
    } catch (err) {
      console.error('Full error object:', err); // More detailed error logging
      console.error('Error response data:', err.response?.data);
      alert(`Failed to update task. ${err.response?.data?.message || err.message}`);
    }
  };
  
  const handleDelete = async (todoId) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;
    
    try {
      const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4MGY5YzBmMmJmODgzYjM2ZTA3YmViYyIsImlhdCI6MTc0NTkxNzI3OSwiZXhwIjoxNzQ4NTA5Mjc5fQ.5MxYozNKwnfQl8IdbJyc3Pl1F3zrc8L7TpDZ1X-kQwg'; // Use the token directly here
      console.log('Deleting todo with ID:', todoId); // Debug log
      
      await axios.delete(
        `http://localhost:5000/api/v1/todos/${todoId}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        }
      );
      
      setTodos(todos.filter(todo => todo._id !== todoId));
    } catch (err) {
      console.error('Full error object:', err);
      console.error('Error response data:', err.response?.data);
      alert(`Failed to delete task. ${err.response?.data?.message || err.message}`);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <>
      <div className="card-container">
        {todos.map((todo) => (
          <div key={todo._id} className="img-wrapper">
            <h2>{todo.title}</h2>
            
            <ul className="side-icons">
              <li title="Update" onClick={() => handleUpdate(todo)}>
                <i className="fas fa-edit" />
              </li>
              <li title="Complete" onClick={() => handleComplete(todo._id)}>
                <i className="fas fa-check" />
              </li>
              <li title="Notes"><i className="fas fa-sticky-note" /></li>
            </ul>

            <div className="card-content">
              <p className="description">{todo.description || 'N/A'}</p>
              
              <div className={`priority-box ${todo.priority.toLowerCase()}`}>
                {todo.priority}
              </div>
              
              {todo.tags && todo.tags.length > 0 && (
                <div className="tags">
                  {todo.tags.map((tag, index) => (
                    <span key={index} className="tag-item">
                      {tag}
                    </span>
                  ))}
                </div>
              )}

              {todo.mentionedUsers && todo.mentionedUsers.length > 0 && (
                <div className="mentions-container">
                  <span className="mention-label">Mentions:</span>
                  {todo.mentionedUsers
                    .filter(userId => userMap[userId])
                    .map(userId => (
                      <span key={userId} className="user-mention">
                        @{userMap[userId]}
                      </span>
                    ))
                  }
                </div>
              )}
              
              {todo.notes && todo.notes.length > 0 && (
                <div className="notes-section">
                  <strong>Notes:</strong>
                  <ul>
                    {todo.notes.map((note, index) => (
                      <li key={index}>
                        {note.content || 'No content'}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>

            <div className="bottom-circle" title="Delete" onClick={() => handleDelete(todo._id)}>
              <i className="fas fa-trash" />
            </div>
          </div>
        ))}
      </div>

      {showUpdateModal && (
        <UpdateCardPopup 
          todo={currentTodo}
          allUsers={allUsers}
          onClose={() => setShowUpdateModal(false)}
          onSubmit={handleUpdateSubmit}
        />
      )}
    </>
  );
};

const UpdateCardPopup = ({ todo, allUsers, onClose, onSubmit }) => {
  const [title, setTitle] = useState(todo?.title || '');
  const [description, setDescription] = useState(todo?.description || '');
  const [priority, setPriority] = useState(todo?.priority || 'Medium');
  const [tags, setTags] = useState(todo?.tags || []);
  const [tagInput, setTagInput] = useState('');
  const [mentionedUsers, setMentionedUsers] = useState(todo?.mentionedUsers || []);
  const [note, setNote] = useState(todo?.notes?.[0]?.content || '');
  const [selectedUser, setSelectedUser] = useState('');

  const handleAddTag = () => {
    if (tagInput.trim()) {
      setTags([...tags, tagInput]);
      setTagInput('');
    }
  };

  const handleMentionUser = (userId) => {
    if (!mentionedUsers.includes(userId)) {
      setMentionedUsers([...mentionedUsers, userId]);
    }
  };

  const handleSubmit = () => {
    const updatedTodo = {
      title,
      description,
      priority,
      tags,
      mentionedUsers,
      notes: [{ content: note }],
    };
    onSubmit(updatedTodo);
  };

  return (
    <div className="update-popup">
      <div className="popup-content">
        <h3>Edit Todo</h3>
        <label>Title</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />

        <label>Description</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} />

        <label>Priority</label>
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>

        <label>Tags</label>
        <input 
          type="text" 
          value={tagInput} 
          onChange={(e) => setTagInput(e.target.value)} 
          onKeyPress={(e) => e.key === 'Enter' && handleAddTag()}
        />
        <div className="tags-list">
          {tags.map((tag, index) => (
            <span key={index} className="tag-item">{tag}</span>
          ))}
        </div>

        <label>Mentions</label>
        <select onChange={(e) => setSelectedUser(e.target.value)} value={selectedUser}>
          <option value="">Select User</option>
          {allUsers.map(user => (
            <option key={user._id} value={user._id}>
              {user.username}
            </option>
          ))}
        </select>
        <button onClick={() => handleMentionUser(selectedUser)}>Add Mention</button>

        <label>Notes</label>
        <textarea value={note} onChange={(e) => setNote(e.target.value)} />

        <div className="popup-actions">
          <button onClick={onClose}>Cancel</button>
          <button onClick={handleSubmit}>Save</button>
        </div>
      </div>
    </div>
  );
};

export default ProfileHover;
