import React, { useState } from 'react';
import '../css/cardmodal.css'; // We'll create this css too

const AddCardModal = ({ setShowModal }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    priority: 'Medium',
    tags: '',
    notes: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('/api/todos', {  // Make sure your backend has POST /api/todos
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Todo added successfully!');
        setShowModal(false);
      } else {
        alert('Error adding todo.');
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Add New Card</h2>
        <form onSubmit={handleSubmit} className="modal-form">
          <input type="text" name="title" placeholder="Title" value={formData.title} onChange={handleChange} required />
          <input type="text" name="description" placeholder="Description" value={formData.description} onChange={handleChange} />
          
          <div className="priority-options">
            <label>
              <input type="radio" name="priority" value="High" checked={formData.priority === 'High'} onChange={handleChange} />
              High
            </label>
            <label>
              <input type="radio" name="priority" value="Medium" checked={formData.priority === 'Medium'} onChange={handleChange} />
              Medium
            </label>
            <label>
              <input type="radio" name="priority" value="Low" checked={formData.priority === 'Low'} onChange={handleChange} />
              Low
            </label>
          </div>

          <input type="text" name="tags" placeholder="Tags (comma separated)" value={formData.tags} onChange={handleChange} />
          <textarea name="notes" placeholder="Notes" value={formData.notes} onChange={handleChange} />

          <div className="modal-buttons">
            <button type="submit">Add</button>
            <button type="button" onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddCardModal;
