import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/card.css'; // make sure CSS is imported

const ProfileHover = () => {
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/v1/todos/todo')
      .then((response) => {
        if (response.data.success) {
          setTodos(response.data.data);
        }
      })
      .catch((err) => {
        setError('Error fetching data');
        console.error(err);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="card-container">
      {todos.map((todo) => (
        <div key={todo._id} className="img-wrapper">
          <h2>{todo.title}</h2>
          
          <ul className="side-icons">
            <li title="Priority"><i className="fas fa-star" /></li>
            <li title="Tags"><i className="fas fa-tags" /></li>
            <li title="Notes"><i className="fas fa-sticky-note" /></li>
          </ul>

          <div style={{ padding: '10px', textAlign: 'center' }}>
            <p style={{ margin: '5px 0' }}>Description: {todo.description || 'N/A'}</p>
            <p style={{ margin: '5px 0' }}>Priority: <strong>{todo.priority}</strong></p>
            {todo.tags.length > 0 && (
              <p style={{ margin: '5px 0' }}>Tags: {todo.tags.join(', ')}</p>
            )}
            {todo.notes.length > 0 && (
              <div>
                <strong>Notes:</strong>
                <ul style={{ paddingLeft: '20px', textAlign: 'left' }}>
                  {todo.notes.map((note, index) => (
                    <li key={index}>
                      <p>{note.content}</p>
                      <small>{new Date(note.createdAt).toLocaleString()}</small>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div className="bottom-circle" title="More">
            <i className="fas fa-arrow-right" />
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProfileHover;
