import React from 'react';
import '../css/card.css';

const Card = ({ name, imgSrc }) => {
  return (
    <div className="img-wrapper">
      <h2>{name}</h2>

      <ul className="side-icons">
        <li>
          <button className="icon-button" aria-label="GitHub">
            <i className="fab fa-github"></i>
          </button>
        </li>
        <li>
          <button className="icon-button" aria-label="Instagram">
            <i className="fab fa-instagram"></i>
          </button>
        </li>
        <li>
          <button className="icon-button" aria-label="Twitter">
            <i className="fab fa-twitter"></i>
          </button>
        </li>
      </ul>

      <img src={imgSrc} alt={`Profile of ${name}`} />

      <div className="bottom-circle" aria-hidden="true">
        <i className="fas fa-plus"></i>
      </div>
    </div>
  );
};

const ProfileHover = () => {
  const cards = [
    { 
      name: "David James", 
      img: "https://images.pexels.com/photos/1220757/pexels-photo-1220757.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Sophia Grace", 
      img: "https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Lucas Oliver", 
      img: "https://images.pexels.com/photos/91227/pexels-photo-91227.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Emma Watson", 
      img: "https://images.pexels.com/photos/774909/pexels-photo-774909.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Noah Mason", 
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Olivia Johnson", 
      img: "https://images.pexels.com/photos/1181686/pexels-photo-1181686.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
  ];

  return (
    <div className="card-container">
      {cards.map((card, index) => (
        <Card key={`${card.name}-${index}`} name={card.name} imgSrc={card.img} />
      ))}
    </div>
  );
};

export default ProfileHover;
