import React, { useState } from 'react';
import '../css/header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faUser, faHeart, faPlus } from '@fortawesome/free-solid-svg-icons';
import AddCardModal from './AddModelCard'; // ðŸ‘ˆ New Modal

const Header = () => {
  const [showModal, setShowModal] = useState(false);

  // Function to handle modal close
  const handleModalClose = () => {
    setShowModal(false); // Close the modal
  };

  return (
    <>
      <header className="header">
        <div className="header-container">
          {/* Left spacer */}
          <div className="header-spacer"></div>

          {/* Centered Search */}
          <div className="search-container">
            <div className="search-bar">
              <input type="text" placeholder="Search products..." />
              <button className="search-button">
                <FontAwesomeIcon icon={faSearch} />
              </button>
            </div>
          </div>

          {/* Right Icons */}
          <div className="icons">
            <a href="#" className="icon-link" aria-label="User account">
              <FontAwesomeIcon icon={faUser} />
            </a>
            <a href="#" className="icon-link" aria-label="Wishlist">
              <FontAwesomeIcon icon={faHeart} />
            </a>
            <button 
              className="icon-link plus-icon" 
              aria-label="Add new card" 
              onClick={() => setShowModal(true)}
            >
              <FontAwesomeIcon icon={faPlus} />
            </button>
          </div>
        </div>
      </header>

      {/* Modal to add new card */}
      {showModal && <AddCardModal setShowModal={setShowModal} onClose={handleModalClose} />}
    </>
  );
};

export default Header;
