import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faPalette,
  faImages,
  faThumbtack,
  faHeart,
  faChartLine,
  faFire,
  faMagic,
  faCircleCheck,
  faCaretUp
} from '@fortawesome/free-solid-svg-icons';
import { faCodepen } from '@fortawesome/free-brands-svg-icons';
import '../css/navBar.css';

const Sidebar = () => {
  const [activeItem, setActiveItem] = useState('Your Work');

  const navItems = [
    { icon: faPalette, label: 'Your Work' },
    { icon: faImages, label: 'Assets' },
    { icon: faThumbtack, label: 'Pinned Items' },
    { icon: faHeart, label: 'Following' },
    { icon: faChartLine, label: 'Trending' },
    { icon: faFire, label: 'Challenges' },
    { icon: faMagic, label: 'Spark' },
  ];

  const calculateHighlightPosition = (label) => {
    const index = navItems.findIndex(item => item.label === label);
    let position = index * 54;
    
    if (index >= 3) position += 16;
    if (index >= 7) position += 16;
    
    return position;
  };

  return (
    <div id="nav-bar">
      <input type="checkbox" id="nav-toggle" />
      <div id="nav-header">
        <a id="nav-title"  target="_blank" rel="noopener noreferrer">
          T<FontAwesomeIcon icon={faCircleCheck} />D<FontAwesomeIcon icon={faCircleCheck} />
        </a>
        <label htmlFor="nav-toggle">
          <span id="nav-toggle-burger"></span>
        </label>
        <hr />
      </div>

      <div id="nav-content">
        {navItems.map((item, index) => (
          <React.Fragment key={item.label}>
            {index === 3 && <hr />}
            {index === 7 && <hr />}
            <div 
              className={`nav-button ${activeItem === item.label ? 'active' : ''}`}
              onClick={() => setActiveItem(item.label)}
              onMouseEnter={() => setActiveItem(item.label)}
            >
              <FontAwesomeIcon icon={item.icon} />
              <span>{item.label}</span>
            </div>
          </React.Fragment>
        ))}
        <div 
          id="nav-content-highlight" 
          style={{ top: `${calculateHighlightPosition(activeItem)}px` }}
        ></div>
      </div>

      <input type="checkbox" id="nav-footer-toggle" />
      <div id="nav-footer">
        <div id="nav-footer-heading">
          <div id="nav-footer-avatar">
            <img src="https://gravatar.com/avatar/4474ca42d303761c2901fa819c4f2547" alt="avatar" />
          </div>
          <div id="nav-footer-titlebox">
            <a id="nav-footer-title" href="https://codepen.io/uahnbu/pens/public" target="_blank" rel="noopener noreferrer">
              uahnbu
            </a>
            <span id="nav-footer-subtitle">Admin</span>
          </div>
          <label htmlFor="nav-footer-toggle">
            <FontAwesomeIcon icon={faCaretUp} />
          </label>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
