import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faCircleExclamation,
  faSignal,
  faTag,
  faCaretUp,
  faCircleCheck
} from '@fortawesome/free-solid-svg-icons';
import '../css/navBar.css';

const Sidebar = () => {
  const [activeItem, setActiveItem] = useState('High Priority');
  const [priorities, setPriorities] = useState([]);
  const [tags, setTags] = useState([]);

  // Mock data - replace with actual API calls
  useEffect(() => {
    // Simulate fetching priority counts
    const fetchPriorities = () => {
      setPriorities([
        { label: 'High Priority', count: 12, icon: faCircleExclamation },
        { label: 'Medium Priority', count: 24, icon: faSignal },
        { label: 'Low Priority', count: 36, icon: faSignal }
      ]);
    };

    // Simulate fetching popular tags
    const fetchTags = () => {
      setTags([
        { label: 'Design', count: 45 },
        { label: 'Development', count: 32 },
        { label: 'Bug', count: 28 },
        { label: 'Feature', count: 21 },
        { label: 'Urgent', count: 15 }
      ]);
    };

    fetchPriorities();
    fetchTags();
  }, []);

  const calculateHighlightPosition = (label) => {
    // Find if the label is in priorities or tags
    const priorityIndex = priorities.findIndex(item => item.label === label);
    if (priorityIndex !== -1) {
      return priorityIndex * 54 + 54; // +54 to account for the section header
    }
    
    const tagIndex = tags.findIndex(item => item.label === label);
    if (tagIndex !== -1) {
      return (priorities.length * 54) + 108 + (tagIndex * 54); // +108 accounts for both section headers and the HR
    }
    
    return 0;
  };

  const handleItemClick = (label) => {
    setActiveItem(label);
  };

  return (
    <div id="nav-bar">
      <input type="checkbox" id="nav-toggle" />
      <div id="nav-header">
        <a id="nav-title" target="_blank" rel="noopener noreferrer">
          T<FontAwesomeIcon icon={faCircleCheck} />D<FontAwesomeIcon icon={faCircleCheck} /> LIST
        </a>
        <label htmlFor="nav-toggle">
          <span id="nav-toggle-burger"></span>
        </label>
        <hr />
      </div>

      <div id="nav-content">
        {/* Priority Section */}
        <div className="nav-section-header">
          <FontAwesomeIcon icon={faCircleExclamation} />
          <span>PRIORITY</span>
        </div>
        {priorities.map((item) => (
          <div 
            key={item.label}
            className={`nav-button ${activeItem === item.label ? 'active' : ''}`}
            onClick={() => handleItemClick(item.label)}
          >
            <FontAwesomeIcon icon={item.icon} />
            <span>{item.label}</span>
            <span className="count-badge">{item.count}</span>
          </div>
        ))}
        
        <hr />
        
        {/* Tags Section */}
        <div className="nav-section-header">
          <FontAwesomeIcon icon={faTag} />
          <span>TAGS</span>
        </div>
        {tags.map((item) => (
          <div 
            key={item.label}
            className={`nav-button ${activeItem === item.label ? 'active' : ''}`}
            onClick={() => handleItemClick(item.label)}
          >
            <FontAwesomeIcon icon={faTag} />
            <span>{item.label}</span>
            <span className="count-badge">{item.count}</span>
          </div>
        ))}
        
        <div 
          id="nav-content-highlight" 
          style={{ 
            top: `${calculateHighlightPosition(activeItem)}px`,
            display: priorities.some(p => p.label === activeItem) || tags.some(t => t.label === activeItem) ? 'block' : 'none'
          }}
        ></div>
      </div>

      <input type="checkbox" id="nav-footer-toggle" />
      <div id="nav-footer">
        <div id="nav-footer-heading">
          <div id="nav-footer-avatar">
            <img src="https://gravatar.com/avatar/4474ca42d303761c2901fa819c4f2547" alt="avatar" />
          </div>
          <div id="nav-footer-titlebox">
            <a id="nav-footer-title" href="https://codepen.io/uahnbu/pens/public" target="_blank" rel="noopener noreferrer">
              uahnbu
            </a>
            <span id="nav-footer-subtitle">Admin</span>
          </div>
          <label htmlFor="nav-footer-toggle">
            <FontAwesomeIcon icon={faCaretUp} />
          </label>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
