import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faPalette,
  faImages,
  faThumbtack,
  faHeart,
  faChartLine,
  faFire,
  faMagic,
  faCircleCheck,
  faCaretUp
} from '@fortawesome/free-solid-svg-icons';
import '../css/navBar.css';

const Sidebar = () => {
  const [activeItem, setActiveItem] = useState('Your Work');
  const [hoverPosition, setHoverPosition] = useState(null);

  const navItems = [
    { icon: faPalette, label: 'Your Work' },
    { icon: faImages, label: 'Assets' },
    { icon: faThumbtack, label: 'Pinned Items' },
    { icon: faHeart, label: 'Following' },
    { icon: faChartLine, label: 'Trending' },
    { icon: faFire, label: 'Challenges' },
    { icon: faMagic, label: 'Spark' },
  ];

  const handleItemHover = (label, event) => {
    setActiveItem(label);
    const itemElement = event.currentTarget;
    const itemRect = itemElement.getBoundingClientRect();
    setHoverPosition(itemRect.top);
  };

  const calculateHighlightPosition = (label) => {
    const index = navItems.findIndex(item => item.label === label);
    return index * 54 + (index < 3 ? 30 : 0);
  };

  return (
    <div id="nav-bar">
      <input type="checkbox" id="nav-toggle" />
      <div id="nav-header">
        <a id="nav-title" target="_blank" rel="noopener noreferrer">
          T<FontAwesomeIcon icon={faCircleCheck} />D<FontAwesomeIcon icon={faCircleCheck} /> LIST
        </a>
        <label htmlFor="nav-toggle">
          <span id="nav-toggle-burger"></span>
        </label>
        <hr />
      </div>

      <div id="nav-content">
        <div className="nav-top-spacer"></div>
        {navItems.map((item, index) => (
          <React.Fragment key={item.label}>
            {index === 3 && <hr />}
            <div 
              className={`nav-button ${activeItem === item.label ? 'active' : ''}`}
              onClick={() => setActiveItem(item.label)}
              onMouseEnter={(e) => handleItemHover(item.label, e)}
              data-index={index}
            >
              <FontAwesomeIcon icon={item.icon} />
              <span>{item.label}</span>
            </div>
          </React.Fragment>
        ))}
        <div 
          id="nav-content-highlight" 
          style={{ top: `${calculateHighlightPosition(activeItem)}px` }}
        ></div>
      </div>

      <div id="nav-footer">
        {/* Footer content remains the same */}
      </div>
    </div>
  );
};

export default Sidebar;
