import React, { useState, useEffect } from 'react';
import '../css/cardModal.css';

const AddCardPopup = ({ onClose }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [tags, setTags] = useState([]);
  const [tagInput, setTagInput] = useState('');
  const [mentionedUsers, setMentionedUsers] = useState([]);
  const [note, setNote] = useState('');
  const [allUsers, setAllUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');

  useEffect(() => {
    const fetchUsers = async () => {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4MGY5YzBmMmJmODgzYjM2ZTA3YmViYyIsImlhdCI6MTc0NTkwOTAxNSwiZXhwIjoxNzQ4NTAxMDE1fQ.udiRiPO6Nq8LI32x21g6M7-7ylBK84MzPgQf669B96g";
    
      try {
        const response = await fetch('http://localhost:5000/api/v1/users', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
    
        const data = await response.json();
        setAllUsers(data.data); // or data.users, depending on your response
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    }

    fetchUsers();
  }, []);

  const handleAddTag = () => {
    if (tagInput.trim()) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
    }
  };

  const handleAddUser = () => {
    if (selectedUser && !mentionedUsers.includes(selectedUser)) {
      setMentionedUsers([...mentionedUsers, selectedUser]);
      setSelectedUser('');
    }
  };

  const handleRemoveUser = (userId) => {
    setMentionedUsers(mentionedUsers.filter(id => id !== userId));
  };

  const handleSubmit = async () => {
    const data = {
      title,
      description,
      priority,
      tags,
      mentionedUsers, // This should be an array of user IDs
      notes: note ? [{ content: note }] : [],
    };

    try {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4MGY5YzBmMmJmODgzYjM2ZTA3YmViYyIsImlhdCI6MTc0NTkwOTAxNSwiZXhwIjoxNzQ4NTAxMDE1fQ.udiRiPO6Nq8LI32x21g6M7-7ylBK84MzPgQf669B96g";

      const response = await fetch('http://localhost:5000/api/v1/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (response.ok) {
        console.log('Todo created successfully:', result.data);
        onClose && onClose();
      } else {
        console.error('Failed to create todo:', result.message);
        alert(`Error: ${result.message}`);
      }
    } catch (err) {
      console.error('Error creating todo:', err);
      alert('An error occurred while creating the todo.');
    }
  };

  return (
    <div className="popup-overlay">
      <div className="popup-card">
        <button className="close-btn" onClick={onClose}>×</button>
        <h2>Add New Card</h2>

        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />

        <div className="priority-section">
          {['High', 'Medium', 'Low'].map((level) => (
            <label key={level}>
              <input
                type="radio"
                name="priority"
                value={level}
                checked={priority === level}
                onChange={(e) => setPriority(e.target.value)}
              /> {level}
            </label>
          ))}
        </div>

        <div className="tags-section">
          <input
            type="text"
            placeholder="Add Tag"
            value={tagInput}
            onChange={(e) => setTagInput(e.target.value)}
          />
          <button type="button" onClick={handleAddTag}>Add Tag</button>
          <div className="tag-list">
            {tags.map((tag, index) => (
              <span key={index} className="tag">{tag}</span>
            ))}
          </div>
        </div>

        <div className="mentioned-users-section">
          <select 
            value={selectedUser} 
            onChange={(e) => setSelectedUser(e.target.value)}
          >
            <option value="">-- Select User --</option>
            {allUsers.map((user) => (
              <option key={user._id} value={user._id}>
                {user.username} ({user.email})
              </option>
            ))}
          </select>
          <button type="button" onClick={handleAddUser}>Add User</button>
          <div className="user-list">
            {mentionedUsers.map((userId) => {
              const user = allUsers.find((u) => u._id === userId);
              return (
                <span key={userId} className="user">
                  {user ? user.username : userId}
                  <button 
                    type="button" 
                    onClick={() => handleRemoveUser(userId)}
                    className="remove-user-btn"
                  >
                    ×
                  </button>
                </span>
              );
            })}
          </div>
        </div>

        <textarea
          placeholder="Notes (optional)"
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />

        <button className="submit-btn" onClick={handleSubmit}>
          Create Card
        </button>
      </div>
    </div>
  );
};

export default AddCardPopup;
