import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTimes } from '@fortawesome/free-solid-svg-icons';
import '../css/card.css';

const Card = ({ name, imgSrc, isAddCard, onClick }) => {
  if (isAddCard) {
    return (
      <div className="img-wrapper add-card" onClick={onClick}>
        <FontAwesomeIcon icon={faPlus} size="3x" />
        <span>Add New Task</span>
      </div>
    );
  }

  return (
    <div className="img-wrapper">
      <h2>{name}</h2>
      <ul className="side-icons">
        <li>
          <button className="icon-button" aria-label="GitHub">
            <i className="fab fa-github"></i>
          </button>
        </li>
        <li>
          <button className="icon-button" aria-label="Instagram">
            <i className="fab fa-instagram"></i>
          </button>
        </li>
        <li>
          <button className="icon-button" aria-label="Twitter">
            <i className="fab fa-twitter"></i>
          </button>
        </li>
      </ul>
      <img src={imgSrc} alt={`Profile of ${name}`} />
      <div className="bottom-circle" aria-hidden="true">
        <i className="fas fa-plus"></i>
      </div>
    </div>
  );
};

const TodoForm = ({ onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    priority: 'Medium',
    tags: '',
    mentionedUsers: [],
    notes: ''
  });
  const [userSearch, setUserSearch] = useState('');
  const [userSuggestions, setUserSuggestions] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleUserSearch = async (e) => {
    const searchTerm = e.target.value;
    setUserSearch(searchTerm);
    
    if (searchTerm.length > 2) {
      try {
        const response = await fetch(`http://localhost:5000/api/v1/users/usernames?search=${searchTerm}`);
        const data = await response.json();
        setUserSuggestions(data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    } else {
      setUserSuggestions([]);
    }
  };

  const addUser = (user) => {
    if (!formData.mentionedUsers.some(u => u._id === user._id)) {
      setFormData(prev => ({
        ...prev,
        mentionedUsers: [...prev.mentionedUsers, user]
      }));
    }
    setUserSearch('');
    setUserSuggestions([]);
  };

  const removeUser = (userId) => {
    setFormData(prev => ({
      ...prev,
      mentionedUsers: prev.mentionedUsers.filter(user => user._id !== userId)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const tagsArray = formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag);
    const todoData = {
      ...formData,
      tags: tagsArray,
      notes: formData.notes ? [{ content: formData.notes }] : []
    };
    onSubmit(todoData);
  };

  return (
    <div className="todo-form-overlay">
      <div className="todo-form-container">
        <button className="close-button" onClick={onClose}>
          <FontAwesomeIcon icon={faTimes} />
        </button>
        <h2>Create New Task</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title*</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Priority</label>
            <div className="radio-group">
              <label>
                <input
                  type="radio"
                  name="priority"
                  value="High"
                  checked={formData.priority === 'High'}
                  onChange={handleChange}
                />
                High
              </label>
              <label>
                <input
                  type="radio"
                  name="priority"
                  value="Medium"
                  checked={formData.priority === 'Medium'}
                  onChange={handleChange}
                />
                Medium
              </label>
              <label>
                <input
                  type="radio"
                  name="priority"
                  value="Low"
                  checked={formData.priority === 'Low'}
                  onChange={handleChange}
                />
                Low
              </label>
            </div>
          </div>

          <div className="form-group">
            <label>Tags (comma separated)</label>
            <input
              type="text"
              name="tags"
              value={formData.tags}
              onChange={handleChange}
              placeholder="design, development, bug"
            />
          </div>

          <div className="form-group">
            <label>Mention Users</label>
            <input
              type="text"
              value={userSearch}
              onChange={handleUserSearch}
              placeholder="Search users..."
            />
            {userSuggestions.length > 0 && (
              <ul className="user-suggestions">
                {userSuggestions.map(user => (
                  <li key={user._id} onClick={() => addUser(user)}>
                    {user.username}
                  </li>
                ))}
              </ul>
            )}
            {formData.mentionedUsers.length > 0 && (
              <div className="selected-users">
                {formData.mentionedUsers.map(user => (
                  <span key={user._id} className="user-tag">
                    {user.username}
                    <button type="button" onClick={() => removeUser(user._id)}>Ã—</button>
                  </span>
                ))}
              </div>
            )}
          </div>

          <div className="form-group">
            <label>Initial Note</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
            />
          </div>

          <button type="submit" className="submit-button">Create Task</button>
        </form>
      </div>
    </div>
  );
};

const ProfileHover = () => {
  const [cards, setCards] = useState([
    { 
      name: "David James", 
      img: "https://images.pexels.com/photos/1220757/pexels-photo-1220757.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Sophia Grace", 
      img: "https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
    { 
      name: "Lucas Oliver", 
      img: "https://images.pexels.com/photos/91227/pexels-photo-91227.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" 
    },
  ]);
  const [showForm, setShowForm] = useState(false);

  const handleAddCardClick = () => {
    setShowForm(true);
  };

  const handleFormSubmit = async (todoData) => {
    try {
      const response = await fetch('http://localhost:5000/api/v1/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(todoData),
      });
      
      if (response.ok) {
        const newTodo = await response.json();
        // Add the new card to the display
        setCards(prev => [...prev, {
          name: newTodo.title,
          img: "https://images.pexels.com/photos/3183150/pexels-photo-3183150.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
        }]);
        setShowForm(false);
      } else {
        console.error('Failed to create todo');
      }
    } catch (error) {
      console.error('Error creating todo:', error);
    }
  };

  return (
    <div className="card-container">
      {cards.map((card, index) => (
        <Card key={`${card.name}-${index}`} name={card.name} imgSrc={card.img} />
      ))}
      <Card 
        isAddCard 
        onClick={handleAddCardClick} 
      />
      {showForm && (
        <TodoForm 
          onClose={() => setShowForm(false)} 
          onSubmit={handleFormSubmit} 
        />
      )}
    </div>
  );
};

export default ProfileHover;
