import React, { useState } from 'react';
import 'font-awesome/css/font-awesome.min.css';
import '../css/navBar.css';

const Sidebar = () => {
  const [activeItem, setActiveItem] = useState('Your Work'); // Default active item

  const navItems = [
    { icon: 'fas fa-palette', label: 'Your Work' },
    { icon: 'fas fa-images', label: 'Assets' },
    { icon: 'fas fa-thumbtack', label: 'Pinned Items' },
    { icon: 'fas fa-heart', label: 'Following' },
    { icon: 'fas fa-chart-line', label: 'Trending' },
    { icon: 'fas fa-fire', label: 'Challenges' },
    { icon: 'fas fa-magic', label: 'Spark' },
    { icon: 'fas fa-gem', label: 'Codepen Pro' }
  ];

  return (
    <div id="nav-bar">
      <input type="checkbox" id="nav-toggle" />
      <div id="nav-header">
        <a id="nav-title" href="https://codepen.io" target="_blank" rel="noopener noreferrer">
          C<i className="fab fa-codepen"></i>DEPEN
        </a>
        <label htmlFor="nav-toggle">
          <span id="nav-toggle-burger"></span>
        </label>
        <hr />
      </div>

      <div id="nav-content">
        {navItems.map((item, index) => (
          <React.Fragment key={item.label}>
            {index === 3 && <hr />}
            {index === 7 && <hr />}
            <div 
              className={`nav-button ${activeItem === item.label ? 'active' : ''}`}
              onClick={() => setActiveItem(item.label)}
              onMouseEnter={() => setActiveItem(item.label)}
            >
              <i className={item.icon}></i>
              <span>{item.label}</span>
            </div>
          </React.Fragment>
        ))}
        <div 
          id="nav-content-highlight" 
          style={{ 
            top: `${navItems.findIndex(item => item.label === activeItem) * 54 + 
                  (activeItem === 'Following' ? 16 : 0) + 
                  (activeItem === 'Codepen Pro' ? 32 : 0)}px` 
          }}
        ></div>
      </div>

      <input type="checkbox" id="nav-footer-toggle" />
      <div id="nav-footer">
        <div id="nav-footer-heading">
          <div id="nav-footer-avatar">
            <img src="https://gravatar.com/avatar/4474ca42d303761c2901fa819c4f2547" alt="avatar" />
          </div>
          <div id="nav-footer-titlebox">
            <a id="nav-footer-title" href="https://codepen.io/uahnbu/pens/public" target="_blank" rel="noopener noreferrer">
              uahnbu
            </a>
            <span id="nav-footer-subtitle">Admin</span>
          </div>
          <label htmlFor="nav-footer-toggle">
            <i className="fas fa-caret-up"></i>
          </label>
        </div>
        <div id="nav-footer-content">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
